var d = new Date(msg.payload);

var day=d.getDate();  //day as a number 1 -31
var month=d.getMonth(); //month as a number 0-11
var year=String(d.getFullYear()); //year as a four digit number (yyyy)
var hours=d.getHours(); //the hour (0-23)
var currDate=String(day + "_" + (month+1) + "_" + year); //the current date as a string (dd_mm_yyyy)

//make sure date will be correct - need to set the hours behind by 6 (since update frequency is 6 hours behind UTC)
if(Math.floor(hours/6) == 0){
    //if Jan 1
    if(month == 1 && day == 1){
        year -= 1;
        month = 12;
        day = 31;
    }
    //if previous month has 30 days && day 1 of current month
    else if((month == 5 || month == 7 || month == 8 || month == 10 || month == 12) && day == 1){
        month -= 1;
        day = 30;
    }
    //if March 1
    else if(month == 3 && day == 1){
        month -= 1;
        //check leap year
        if(year % 4 == 0){
            day = 29;
        }
        else{
            day = 28;
        }
    }
    //if previous month had 31 days && day 1 of current month
    else if((month == 2 || month == 4 || month == 6 || month == 9 || month == 11) && day == 1){
        month -= 1;
        day = 31;
    }
    else{
        day -= 1;
    }
    hours = 18;
}
else{
    hours -= 6;
}

//Making the date a valid format
newPayload = String("https://nomads.ncep.noaa.gov/pub/data/nccf/com/gfs/prod/gfs."); //the payload for the download command
newPayload += year; //add the year

//month formatting
if(month < 10)
{
    newPayload  += String("0"+(month+1));
}
else
{
    newPayload += String(month+1);
}

//day formatting
if(day < 10)
{
    newPayload += String("0"+day);
}
else
{
    newPayload += String(day);
}

//Finding out what time of day to download the data

//ref hour
var refHours = Math.floor(hours / 6);
var quarter = ""; //holds the formatted hours (quarter of day)
if(refHours < 2){   //'00' or '06'
    quarter = String("0" + refHour*6);
}
else{               //'12' or '18'
    quarter = String(refHours*6);
}

//recorded hour
var recHours = Math.floor(hours / 3) * 3;

var hourWithinRef = Math.abs(recHours - (refHours * 6)); //multiple refHours * 6 so that it is formatted correctly *NOTE will always be 000 or 003

//adds to the command format
newPayload += String("/" + quarter + "/atmos/gfs.t" + quarter + "z.pgrb2.1p00.f00" + hourWithinRef); //full download url
var fileName = String("gfs.t" + quarter + "z.pgrb2.1p00.f00" + hourWithinRef); //fileName formatting
var filePath = String("/data/ecmwf-data/" + currDate + "/" + quarter + "/00" + hourWithinRef + "/"); //filePath formatting

var dirMsg = []; //directory creation array
var downMsg = []; //download command array
var renameMsg = []; //rename to correct format

dirMsg.push({payload:String(filePath)});
//https://nomads.ncep.noaa.gov/pub/data/nccf/com/gfs/prod/gfs. 20230322/12/atmos/
downMsg.push({payload:String("-P " + filePath + " " + newPayload)});
renameMsg.push({payload:String(filePath + fileName + " " + filePath + "raw_data.grib2")});

//output1 = array of commands to make directory; output2 = array of commands to download each of the netCDF data files
return [dirMsg, downMsg, renameMsg];
