var d = new Date(msg.payload);
d.setHours(d.getHours() - 6); //need to set the hours behind by 6 (since update frequency is 6 hours behind UTC)
var day=d.getDate();  //day as a number 1 -31
var month=d.getMonth(); //month as a number 0-11
var year=String(d.getFullYear()); //year as a four digit number (yyyy)
var hours=d.getHours(); //the hour (0-23)
var currDate=String(day + "_" + (month+1) + "_" + year); //the current date as a string (dd_mm_yyyy)

//Making the date a valid format
newPayload = String("https://nomads.ncep.noaa.gov/pub/data/nccf/com/gfs/prod/enkfgdas."); //the payload for the download command
newPayload += year; //add the year

//month formatting
if(month < 10)
{
    newPayload  += String("0"+(month+1));
}
else
{
    newPayload += String(month+1);
}

//day formatting
if(day < 10)
{
    newPayload += String("0"+day);
}
else
{
    newPayload += String(day);
}

//Finding out what time of day to download the data
var modHours = Math.floor(hours / 6);
var quarter = ""; //holds the formatted hours (quarter of day)
if(modHours < 2){   //'00' or '06'
    quarter = String("0" + modHours*6);
}
else{               //'12' or '18'
    quarter = String(modHours*6);
}

//adds to the command format
newPayload += String("/" + quarter + "/atmos/mem");

var dirMsg = []; //directory creation array
var downMsg = []; //download command array

for(let i=1; i <= 80; i++){ //run for each of the folders of data (80 times)
    if(i<10){ //formatting of folders < 10
        dirMsg.push({payload:String("/data/ecmwf-data/" + currDate + "/" + quarter + "/mem00" + i)});
        //the 3 commands for downloading
        for(let j=1; j<=3; j++){
            downMsg.push({payload:String("-P /data/ecmwf-data/" + currDate + "/" + quarter + "/mem00" + i + "/ " + newPayload + "00" + i + "/gdas.t" + quarter + "z.sfcf00" + j*3 + ".nc")});
        }
    }
    else{
        dirMsg.push({payload:String("/data/ecmwf-data/" + currDate + "/" + quarter + "/mem0" + i)});
        //the 3 commands for downloading
        for(let j=1; j<=3; j++){
            downMsg.push({payload:String("-P /data/ecmwf-data/" + currDate + "/" + quarter + "/mem0" + i + "/ " + newPayload + "0" + i + "/gdas.t" + quarter + "z.sfcf00" + j*3 + ".nc")});
        }
    }
}

//output1 = array of commands to make directory; output2 = array of commands to download each of the netCDF data files
return [dirMsg, downMsg];
